mapper.select:
select * from 
		inn
			join pet
				on inn.inn_petnum = pet.petnum
				where inn_user_mail = #{mail}
					and inn_st_date >= #{today}
					and inn_isRe = 'Y'
					order by inn_st_date asc
					limit  #{cri.startPage}, #{cri.perPageNum}

mapper.update:
update inn
	set inn_isRe = 'N'
		where inn_num = #{inn.inn_num}
